import React, {useState} from "react";

function Item({name, description, image, onItemDelete, id}){
    const [isClicked, setIsClicked] = useState(false)

    if(isClicked === false){
        return getImage()
    }

    function getImage(){
        return <img src={image} alt={name} height="400px" width="400px" onClick={() => setIsClicked(!isClicked)}/>
    }

    return (
        <div id="item">
            <h1>{name}</h1>
            <p>{description}</p>
            <img src={image} alt={name} height="400px" width="400px" onClick={() => setIsClicked(!isClicked)}/><br />
            <button onClick={() => onItemDelete(id)}>DELETE</button>
        </div>
    )
}


export default Item
