import React, {useState} from "react";

function NewItem({onFormSubmit}){
    const [formData, setFormData] = useState({
        name : "",
        description: ""
    })

    function updateForm(e, input){
        setFormData({...formData, [input]: e.target.value})
    }

    console.log(formData)

    function handleSubmit(e){
        e.preventDefault()
        onFormSubmit()
    }

    return <div id="new-item">
        <h1>New Item</h1>
        <form id="new-item-form" onSubmit={handleSubmit}>
            <label htmlFor="name">Name: </label>
            <input name="name" type="text" placeholder="Enter Name" value={formData.name} onChange={e => updateForm(e, "name")}></input> <br></br>
            <label htmlFor="description">Description: </label>
            <input name="description" type="text" placeholder="Description" value={formData.description} onChange={e => updateForm(e, "description")} ></input><br></br>
            <input type="submit" value="Submit"></input>
        </form>
    </div>
}

export default NewItem
